local SCREENWIDTH = 240
local SCREENHEIGHT = 136

local MOVESPEED = 0.1
local ZOOMSPEED = 0.001
local INITANIMSPEED = 50
local ANIMSPEEDSCALE = 0.000002

local CURVECOUNT = 256
local CURVESTEP = 4
local ITERATIONS = 256

local ANG1INC = CURVESTEP * 2 * math.pi / 235
local ANG2INC = CURVESTEP
local SCREENXCENTRE = SCREENWIDTH/2
local SCREENYCENTRE = SCREENHEIGHT/2
local INITSCALE = SCREENHEIGHT/4

local COLINC = 4 / ITERATIONS

local scale = INITSCALE
local animSpeed = INITANIMSPEED
local xOffset = 0
local yOffset = 0
local animationTime = 0

local startTime = time()

function SetPaletteEntry(index, r, g, b)
    if index >= 0 and index < 16 then
        poke(0x3FC0 + index*3, r)
        poke(0x3FC1 + index*3, g)
        poke(0x3FC2 + index*3, b)
    end
end

function SetupPalette()
    local index = 0;
    for i = 0, 3 do
        local red = (i * 255) / 3

        for j = 0, 3 do
            local green = (j * 255) / 3
            local blue = (512-(red+green))/2

            SetPaletteEntry(i*4+j, red, green, blue)
        end
    end
end

function Controls(deltaTime)
    if btn(0) then yOffset = yOffset + MOVESPEED * deltaTime end
    if btn(1) then yOffset = yOffset - MOVESPEED * deltaTime end
    if btn(2) then xOffset = xOffset + MOVESPEED * deltaTime end
    if btn(3) then xOffset = xOffset - MOVESPEED * deltaTime end
    if btn(4) then
        scale = scale * (1 + ZOOMSPEED * deltaTime)
        xOffset = xOffset * (1 + ZOOMSPEED * deltaTime)
        yOffset = yOffset * (1 + ZOOMSPEED * deltaTime)
    end
    if btn(5) then
        scale = scale / (1 + ZOOMSPEED * deltaTime)
        xOffset = xOffset / (1 + ZOOMSPEED * deltaTime)
        yOffset = yOffset / (1 + ZOOMSPEED * deltaTime)
    end
    if btnp(6, 30, 0) then animSpeed = animSpeed + 1 end
    if btnp(7, 30, 0) then animSpeed = animSpeed - 1 end
end

function BOOT()
    SetupPalette()
end

function TIC()
    local deltaTime = time() - startTime;
    startTime = time()
    animationTime = animationTime + animSpeed * ANIMSPEEDSCALE * deltaTime

    cls(0)

    local xOrigin = SCREENXCENTRE + xOffset
    local yOrigin = SCREENYCENTRE + yOffset
    local ang1Start = animationTime
    local ang2Start = animationTime
    for i = 0, CURVECOUNT-1, CURVESTEP do
        local x, y = 0, 0
        local col = math.floor((i*4)/CURVECOUNT)*4;
        for j = 0, ITERATIONS-1 do
            local ang1 = ang1Start + x
            local ang2 = ang2Start + y
            x = math.sin(ang1) + math.sin(ang2)
            y = math.cos(ang1) + math.cos(ang2)
            pix(xOrigin+x*scale, yOrigin+y*scale, col < 1 and 1 or col)
            col = col + COLINC
        end

        ang1Start = ang1Start + ANG1INC
        ang2Start = ang2Start + ANG2INC
    end

    Controls(deltaTime)

    print(math.floor(time()-startTime).."ms", 0, 0, 4)
end

